import React, { useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  SelectChangeEvent,
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface GoalFormData {
  title: string;
  description: string;
  dailyTimeAvailable: number;
  startDate: Date | null;
  endDate: Date | null;
  difficulty: string;
}

const GoalForm: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<GoalFormData>({
    title: '',
    description: '',
    dailyTimeAvailable: 60,
    startDate: new Date(),
    endDate: null,
    difficulty: 'medium',
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/goals', formData);
      console.log('Goal created:', response.data);
      navigate('/calendar');
    } catch (error) {
      console.error('Error creating goal:', error);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name as string]: value,
    }));
  };

  const handleSelectChange = (e: SelectChangeEvent) => {
    setFormData(prev => ({
      ...prev,
      difficulty: e.target.value,
    }));
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography variant="h4" gutterBottom>
          Create New Goal
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              name="title"
              label="Goal Title"
              value={formData.title}
              onChange={handleChange}
              required
              fullWidth
            />

            <TextField
              name="description"
              label="Goal Description"
              value={formData.description}
              onChange={handleChange}
              multiline
              rows={4}
              required
              fullWidth
            />

            <TextField
              name="dailyTimeAvailable"
              label="Daily Time Available (minutes)"
              type="number"
              value={formData.dailyTimeAvailable}
              onChange={handleChange}
              required
              fullWidth
            />

            <DatePicker
              label="Start Date"
              value={formData.startDate}
              onChange={(newValue) => setFormData(prev => ({ ...prev, startDate: newValue }))}
            />

            <DatePicker
              label="End Date"
              value={formData.endDate}
              onChange={(newValue) => setFormData(prev => ({ ...prev, endDate: newValue }))}
            />

            <FormControl fullWidth>
              <InputLabel>Difficulty Level</InputLabel>
              <Select
                name="difficulty"
                value={formData.difficulty}
                onChange={handleSelectChange}
                label="Difficulty Level"
              >
                <MenuItem value="easy">Easy</MenuItem>
                <MenuItem value="medium">Medium</MenuItem>
                <MenuItem value="hard">Hard</MenuItem>
              </Select>
            </FormControl>

            <Button
              type="submit"
              variant="contained"
              color="primary"
              size="large"
              sx={{ mt: 2 }}
            >
              Create Goal Plan
            </Button>
          </Box>
        </form>
      </Paper>
    </Container>
  );
};

export default GoalForm;
